import React, { Component } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import HousekeepingCard from './HousekeepingCard';
import './show.css';

class Housekeeping extends Component {
  constructor(props) {
    super(props);
    this.state = {
      servant: null,
      role: props.role,
      id: props.id
    };
  }

  componentDidMount() {
    axios({
      method: 'post',
      url: 'dashboard/housekeeping',
      baseURL: 'http://localhost:8082/',
      data: {
        role: this.props.role,
        id: this.props.id
      }
    }).then(res => {
      const servant = res.data.find(serv => serv.servantid === this.props.id);
      if (servant && servant.request !== 'no') {
        this.setState({ servant });
      }
    }).catch(err => {
      console.log('Error from Housekeeping.js');
    });
  }

  dismissRequest(sid) {
    axios({
      method: 'post',
      url: 'housekeeping/request/dismiss',
      baseURL: 'http://localhost:8082/',
      data: {
        sid: sid
      }
    }).then(() => {
      alert('Request dismissed');
      this.setState({ servant: null }); // Hide the servant after dismissing
    }).catch(err => {
      console.log('Error from dismissRequest');
    });
  }

  render() {
    const { servant } = this.state;

    if (!servant) {
      return (
        <div className="textz">
          <h1>Sorry, no staff is available right now.</h1>
        </div>
      );
    }

    return (
      <div className="label label-danger" style={{ margin: 'auto', marginTop: '100px' }}>
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <h2 className="display-4 text-center">Available Staff</h2>
            </div>
            <div className="list" style={{ margin: 'auto' }}>
              <HousekeepingCard
                servant={servant}
                role={this.state.role}
                id={this.state.id}
                onDismiss={this.dismissRequest.bind(this)}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Housekeeping;
