import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import './components/show.css';

import Home from './components/Home';
import Login from './components/Login';
import Contact from './components/Contact';
import Dashboard from './components/Dashboard';
import SignUp from './components/SignUp';
import withAuthorization from './components/withAuthorization';
import Register from './components/Register';
import QuarterBookingPage from "./QuarterBookingPage";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = JSON.parse(localStorage.getItem("user")) || {
      user: { username: "", fname: "", lname: "", role: "", userid: "" }
    };
    if (!localStorage.getItem("user")) {
      localStorage.setItem("user", JSON.stringify(this.state));
    }
  }

  render() {
    const AdminPage = withAuthorization(SignUp, 'admin');

    return (
      <Router>
        <Switch>
          <Route exact path='/' component={Home} />
          <Route path='/register' component={Register} />
          <Route path='/quarter-bookings' component={QuarterBookingPage} />
          <Route path='/login' render={() => <Login user={this.state.user} />} />
          <Route path='/contact' component={Contact} />
          <Route path='/dashboard' render={() => <Dashboard user={this.state.user} />} />
          <Route path='/admin' component={AdminPage} />
        </Switch>
      </Router>
    );
  }
}

export default App;
