import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ManageBooking.css'; // Ensure CSS is imported

function ManageBooking(props) {
  const [requestDetails, setRequestDetails] = useState("");
  const [facultyBookings, setFacultyBookings] = useState([]);

  // Fetch faculty-specific bookings on component load
 
  useEffect(() => {
    if (props.user?.userid) {
      axios
      axios
      .get(`http://localhost:8082/faculty-bookings/${props.user.userid}`)
      .then((response) => {
        console.log("Bookings Data:", response.data); // Debug output
        setFacultyBookings(response.data);
       })
      //   .get(`http://localhost:8082/faculty-bookings/${props.user.userid}`)
      //   .then((response) => setFacultyBookings(response.data))
      //   .catch((error) => console.error("Error fetching bookings:", error));
    }
    
  }, [props.user?.userid]);
  
  const handleSubmit = async (e) => {
  e.preventDefault();

  const payload = {
    userid: props.user?.userid || '',
    faculty_name: props.user?.fname || '',
    faculty_id: props.user?.userid || '',
    department: 'IT', // Replace with dynamic data if available
    faculty_level: 'Assistant Professor', // Example, make dynamic if needed
    years_at_BIT: 3, // Example, make dynamic if needed
    family_members: 4, // Example, make dynamic if needed
    quarters_type: 'Type A', // Example, make dynamic if needed
    contact_no: '9876543210', // Example, make dynamic if needed
    email_id: props.user?.email || '',
    request: requestDetails,
  };

  try {
    const response = await fetch("http://localhost:8082/faculty-booking", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    const data = await response.json();
    if (data.status === "success") {
      alert("Booking request submitted successfully!");
      setRequestDetails(""); // Clear form input
      fetchBookings(); // Refresh booking list
    } else {
      alert("Failed to submit request.");
    }
  } catch (error) {
    console.error("Error submitting booking request:", error);
  }
};
const fetchBookings = () => {
  if (props.user?.userid) {
    axios
      .get(`http://localhost:8082/faculty-bookings/${props.user.userid}`)
      .then((response) => setFacultyBookings(response.data))
      .catch((error) => console.error("Error fetching bookings:", error));
  }
};

useEffect(() => {
  fetchBookings();
}, [props.user?.userid]);


  return (
    <div className="manage-booking-container">
      <h2 className="heading">Request Faculty Booking</h2>
      <form onSubmit={handleSubmit} className="manage-booking-form">
        <div className="form-group">
          <label htmlFor="request-details">Booking Details</label>
          <textarea
            id="request-details"
            className="form-control"
            rows="4"
            value={requestDetails}
            onChange={(e) => setRequestDetails(e.target.value)}
            placeholder="Enter your booking request details here..."
          />
        </div>
        <button type="submit" className="btn btn-primary btn-lg">
          Submit Request
        </button>
      </form>

      <h3>Your Booking Requests</h3>
      <div className="booking-list">
        {facultyBookings.length === 0 ? (
          <p>No bookings available.</p>
        ) : (
          facultyBookings.map((booking) => (
            <div className="booking-card" key={booking.bookingId}>
              <h4>Booking ID: {booking.bookingId}</h4>
              <p><strong>Request:</strong> {booking.request_details}</p>
              <p><strong>Status:</strong> {booking.status}</p>
              {booking.status === "Approved" && (
                <p><strong>Assigned Quarter:</strong> {booking.assignedQuarter}</p>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default ManageBooking;
