import React, { useEffect, useState } from "react";
import axios from "axios";
import './ManageBooking.css';

const AdminManageBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8082/faculty-bookings")
      .then((response) => {
        setBookings(response.data);
      })
      .catch((error) => console.error("Error fetching bookings:", error));
  }, []);

  const handleApproval = (bookingId, status) => {
    axios
      .post("http://localhost:8082/faculty-booking/approve", {
        bookingId,
        status,
        assignedQuarter: "BHK 2BHK", // example value
        adminComments: "Approved by Admin",
      })
      .then((response) => {
        if (response.data.status === "success") {
          alert("Booking approved!");
        }
      })
      .catch((error) => console.error("Error approving booking:", error));
  };

  return (
    <div className="admin-manage-booking">
      <h3>Manage Faculty Bookings</h3>
      {bookings.length === 0 ? (
        <p>No pending bookings.</p>
      ) : (
        <ul className="booking-list">
          {bookings.map((booking) => (
            <li key={booking.bookingId} className="booking-item">
              <p>Booking ID: {booking.bookingId}</p>
              <p>Faculty Name: {booking.faculty_name}</p>
              <p>Request: {booking.request}</p>
              <button 
                className="btn btn-success" 
                onClick={() => handleApproval(booking.bookingId, "Approved")}
              >
                Approve
              </button>
              <button 
                className="btn btn-danger" 
                onClick={() => handleApproval(booking.bookingId, "Rejected")}
              >
                Reject
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AdminManageBookings;
