import React, { Component } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import Header from './Header';
import './show.css';

const HousekeepingCard = ({ servant, role, id, onDismiss }) => {
  return (
    <div className="housekeeping-card">
      <div className="housekeeping-card-header">
        <span className="housekeeping-card-title">Anand</span>
        <button className="housekeeping-card-dismiss" onClick={() => onDismiss(servant.servantid)}>
          &times;
        </button>
      </div>
      <div className="housekeeping-card-body">
        <p className="housekeeping-card-description">For Washing</p>
        <p className="housekeeping-card-request">Requested by: {servant.request}</p>
      </div>
    </div>
  );
};


class Housekeeping extends Component {
  constructor(props) {
    super(props);
    this.state = {
      servant: null,
      role: props.role,
      id: props.id
    };
  }

  componentDidMount() {
    axios({
      method: 'post',
      url: 'dashboard/housekeeping',
      baseURL: 'http://localhost:8082/',
      data: {
        role: this.props.role,
        id: this.props.id
      }
    }).then(res => {
      const servant = res.data.find(serv => serv.servantid === this.props.id);
      if (servant && servant.request !== 'no') {
        this.setState({ servant });
      }
    }).catch(err => {
      console.log('Error from Housekeeping.js');
    });
  }

  dismissRequest(sid) {
    axios({
      method: 'post',
      url: 'housekeeping/request/dismiss',
      baseURL: 'http://localhost:8082/',
      data: {
        sid: sid
      }
    }).then(() => {
      alert('Request dismissed');
      this.setState({ servant: null }); // Hide the servant after dismissing
    }).catch(err => {
      console.log('Error from dismissRequest');
    });
  }

  render() {
    const { servant } = this.state;

    if (!servant) {
      return (
        <div className="textz">
          <h1>Sorry, no staff is available right now.</h1>
        </div>
      );
    }

    return (
      <div className="label label-danger" style={{ margin: 'auto', marginTop: '100px' }}>
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <h2 className="display-4 text-center">Available Staff</h2>
            </div>
            <div className="list" style={{ margin: 'auto' }}>
              <HousekeepingCard
                servant={servant}
                role={this.state.role}
                id={this.state.id}
                onDismiss={this.dismissRequest.bind(this)}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

class RequestHousekeeping extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {
        sid: this.props.match.params.sid,
        uid: this.props.id
      }
    };
  }

  submitHandler(event) {
    event.preventDefault();

    axios({
      method: 'post',
      url: 'housekeeping/request/confirm',
      baseURL: 'http://localhost:8082/',
      data: {
        servant: this.state.data
      }
    }).then(() => {
      alert('Successfully requested');
      window.location.replace('/dashboard/housekeeping');
    }).catch(err => {
      console.log('Error submitting request');
    });
  }

  render() {
    const backbtn = () => {
      window.location.replace('/dashboard/housekeeping');
    };
    
    return (
      <div>
        <center><h2>The Housekeeping staff will be at your doorstep soon...</h2></center>
        <br />
        <Button style={{ float: 'left', fontSize: '18px', marginLeft: '10%' }} variant="warning" onClick={backbtn}><b>&larr; Back</b></Button>
        <br /><br /><br />
        <center>
          <Button onClick={this.submitHandler.bind(this)} variant="primary">Confirm Request</Button><br />
          <img alt='image1' src="https://media.istockphoto.com/vectors/cleaning-service-staff-smiling-cartoon-characters-isolated-on-white-vector-id958689426?k=6&m=958689426&s=612x612&w=0&h=KW24AVt3IX-yDa3LflcocU82Hn814BMU5W3AcbUV8us=" height='500px' width='500px' />
          <br /><br />
        </center>
      </div>
    );
  }
}

export { Housekeeping, RequestHousekeeping };
