import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import Header from "./Header";

function QuartersBooking(props) {
  const history = useHistory();
  const [requestDetails, setRequestDetails] = useState("");
  const [loading, setLoading] = useState(true);

  // When component mounts, check if a booking request already exists for this user
  useEffect(() => {
    async function checkBooking() {
      try {
        const response = await fetch(`http://localhost:8082/faculty-bookings/${props.user.userid}`);
        const data = await response.json();
        console.log("API Call Success: Bookings Data:", data);
        // If any booking exists, redirect to ManageBooking page
        if (data.length > 0) {
          history.push("/dashboard/manage-booking");
        } else {
          setLoading(false);
        }
      } catch (error) {
        console.error("Error checking booking:", error);
        setLoading(false);
      }
    }
    if (props.user?.userid) {
      checkBooking();
    } else {
      console.log("No user ID found.");
      setLoading(false);
    }
  }, [props.user, props.user.userid, history]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      userid: props.user.userid,
      faculty_name: props.user.fname,
      faculty_id: props.user.userid, // Adjust if faculty_id is different from userid
      department: "IT", // Replace with dynamic data if available
      faculty_level: "Assistant Professor", // Example value
      years_at_BIT: 3, // Example value
      family_members: 4, // Example value
      quarters_type: "Type A", // Example value
      contact_no: "9876543210", // Example value
      email_id: props.user.email || "",
      request: requestDetails,
    };

    try {
      const response = await fetch("http://localhost:8082/faculty-booking", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (data.status === "success") {
        alert("Booking request submitted successfully!");
        // After successful submission, redirect to ManageBooking page
        history.push("/dashboard/manage-booking");
      } else {
        alert("Failed to submit request.");
      }
    } catch (error) {
      console.error("Error submitting booking request:", error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Header />
      <div style={{ width: "400px", margin: "auto", marginTop: "50px" }}>
        <h3>Faculty Quarters Booking</h3>
        <form onSubmit={handleSubmit}>
          {/* Optionally, display non-editable fields for user info */}
          <div className="form-group">
            <label>Faculty Name</label>
            <input
              type="text"
              className="form-control"
              value={props.user.fname}
              disabled
            />
          </div>
          <div className="form-group">
            <label>Faculty ID</label>
            <input
              type="text"
              className="form-control"
              value={props.user.userid}
              disabled
            />
          </div>
          {/* Additional form fields can be added here if needed */}
          <div className="form-group">
            <label>Booking Details</label>
            <textarea
              className="form-control"
              rows="4"
              value={requestDetails}
              onChange={(e) => setRequestDetails(e.target.value)}
              placeholder="Enter your booking request details here..."
              required
            />
          </div>
          <button type="submit" className="btn btn-primary btn-lg btn-block">
            Submit Request
          </button>
        </form>
      </div>
    </div>
  );
}

export default QuartersBooking;
